<?php $host=getenv('DB_HOST')?:'127.0.0.1';$port=getenv('DB_PORT')?:'3306';$dbname=getenv('DB_NAME')?:'contactforms';$username=getenv('DB_USER')?:'appuser';$password=getenv('DB_PASS')?:'';$ca='/etc/ssl/certs/ca-certificates.crt';$dsn=sprintf('mysql:host=%s;port=%s;dbname=%s;charset=utf8mb4',$host,$port,$dbname);try{$pdo=new PDO($dsn,$username,$password,[PDO::ATTR_ERRMODE=>PDO::ERRMODE_EXCEPTION,PDO::ATTR_DEFAULT_FETCH_MODE=>PDO::FETCH_ASSOC,PDO::MYSQL_ATTR_SSL_CA=>$ca,]);}catch(PDOException $e){http_response_code(500);die('Database connection failed.');}?>