<?php require __DIR__.'/db_config.php';$pdo->exec("CREATE TABLE IF NOT EXISTS orders(id INT AUTO_INCREMENT PRIMARY KEY,firstname VARCHAR(100) NOT NULL,lastname VARCHAR(100),mead VARCHAR(50) NOT NULL,quantity INT NOT NULL,created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,INDEX idx_created_at (created_at)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;");if($_SERVER['REQUEST_METHOD']==='POST'){ $f=trim($_POST['firstname']??'');$l=trim($_POST['lastname']??'');$m=trim($_POST['mead']??'');$q=intval($_POST['quantity']??0);if($f===''||$m===''||$q<1||$q>10){http_response_code(400);echo "Invalid input. <a href='index.html'>Back</a>";exit;} $st=$pdo->prepare("INSERT INTO orders(firstname,lastname,mead,quantity) VALUES(?,?,?,?)");$st->execute([$f,$l,$m,$q]);header('Location: /view_orders.php');exit;}?><!doctype html><html><body><p>Only POST submissions are allowed. <a href='/index.html'>Back</a></p></body></html>