<?php require __DIR__.'/db_config.php';$pdo->exec("CREATE TABLE IF NOT EXISTS orders(id INT AUTO_INCREMENT PRIMARY KEY,firstname VARCHAR(100) NOT NULL,lastname VARCHAR(100),mead VARCHAR(50) NOT NULL,quantity INT NOT NULL,created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,INDEX idx_created_at (created_at)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;");$rows=$pdo->query("SELECT * FROM orders ORDER BY created_at DESC")->fetchAll();?><!doctype html><html><body><h2>Orders</h2><table border='1' cellspacing='0' cellpadding='6'><tr><th>ID</th><th>First</th><th>Last</th><th>Mead</th><th>Qty</th><th>Created</th></tr><?php foreach($rows as $o): ?><tr><td><?=htmlspecialchars($o['id'])?></td><td><?=htmlspecialchars($o['firstname'])?></td><td><?=htmlspecialchars($o['lastname'])?></td><td><?=htmlspecialchars($o['mead'])?></td><td><?=htmlspecialchars($o['quantity'])?></td><td><?=htmlspecialchars($o['created_at'])?></td></tr><?php endforeach; ?></table><p><a href='/index.html'>Back</a></p></body></html>